//moon.vue
<template>
  <div class="moon">
    <span>月球</span>
    <div class="mychart" ref="mychart"></div>
  </div>
</template>

<script>
  import * as echarts from 'echarts';
  import 'echarts-gl';
  import url from '../assets/bg3.jpeg'
  export default {
    data() {
      return {
        ROOT_PATH: 'https://cdn.jsdelivr.net/gh/apache/echarts-website@asf-site/examples',
        imgurl: url,
        angle: 0, //自转角度默认为0,每s转10°
      }
    },
    methods: {
      initMoon() {
        let myChart = echarts.init(this.$refs.mychart)
        let option = {
          globe: {
            baseTexture: this.ROOT_PATH + '/data-gl/asset/moon-base.jpg',
            heightTexture: this.ROOT_PATH + '/data-gl/asset/moon-bump.jpg',
            displacementScale: 0.05,
            displacementQuality: 'medium',
            environment: this.imgurl,
            shading: 'realistic',
            realisticMaterial: {
              roughness: 0.8,
              metalness: 0
            },
            postEffect: {
              enable: true,
              SSAO: {
                enable: true,
                radius: 2,
                intensity: 1,
                quality: 'high'
              }
            },
            temporalSuperSampling: {
              enable: true
            },
            light: {
              ambient: {
                intensity: 0
              },
              main: {
                intensity: 2,
                shadow: true
              },
              ambientCubemap: {
                texture: this.ROOT_PATH + '/data-gl/asset/pisa.hdr',
                exposure: 0,
                diffuseIntensity: 0.02
              }
            },
            viewControl: {
              autoRotate: true,
              distance: 1000,
              // 只能纵向旋转
              // rotateSensitivity: [0, 1]
            }
          },
          series: []
        }
        myChart.setOption(option)
      },
    },
    mounted() {
      this.initMoon()
    }
  }
</script>

<style lang="less" scoped>
  .moon {
    display: flex;
    flex-direction: column;
    align-items: center;

    .mychart {
      margin-top: 30px;
      width: 400px;
      height: 400px
    }
  }
</style>
